export declare const MODEL_INSPECTOR_STYLES = "\n  .model-inspector {\n    display: grid;\n    gap: 16px;\n    margin: 4px 0 12px;\n  }\n  .model-inspector__detail {\n    min-width: 0;\n  }\n  .model-detail {\n    border-radius: 18px;\n    border: 1px solid var(--border);\n    background: var(--surface-glass);\n    padding: 18px 20px;\n    box-shadow: 0 18px 36px rgba(15, 23, 42, 0.08);\n  }\n  .model-detail__header {\n    display: flex;\n    flex-wrap: wrap;\n    align-items: flex-start;\n    column-gap: 16px;\n    row-gap: 10px;\n  }\n  .model-detail__header > div:first-child {\n    flex: 1 1 260px;\n    min-width: 0;\n  }\n  .model-detail__header h3 {\n    margin: 0;\n    font-size: 1.1rem;\n    color: var(--text);\n  }\n  .model-detail__tagline {\n    margin: 4px 0 0;\n    font-size: 0.88rem;\n    color: var(--subtle);\n  }\n  .model-detail__cost {\n    font-weight: 600;\n    color: var(--accent);\n    font-size: 0.9rem;\n    margin-left: auto;\n    text-align: right;\n    line-height: 1.35;\n    white-space: normal;\n  }\n  .model-detail__description {\n    margin: 12px 0 14px;\n    color: var(--muted);\n    line-height: 1.6;\n  }\n  .model-detail__facts {\n    display: grid;\n    gap: 10px;\n    margin: 0;\n    padding: 0;\n  }\n  .model-detail__facts div {\n    display: grid;\n    gap: 4px;\n  }\n  .model-detail__facts dt {\n    margin: 0;\n    font-size: 0.7rem;\n    text-transform: uppercase;\n    letter-spacing: 0.08em;\n    color: var(--subtle);\n  }\n  .model-detail__facts dd {\n    margin: 0;\n    font-weight: 500;\n    color: var(--text);\n  }\n  .model-highlight {\n    display: inline-flex;\n    align-items: center;\n    padding: 0.2rem 0.6rem;\n    border-radius: 999px;\n    background: var(--accent-soft);\n    color: var(--accent-dark);\n    font-size: 0.75rem;\n    font-weight: 600;\n  }\n  .model-lineup {\n    display: flex;\n    flex-direction: column;\n    gap: 8px;\n  }\n  .model-lineup[hidden] {\n    display: none;\n  }\n  .model-lineup__title {\n    font-size: 0.72rem;\n    letter-spacing: 0.08em;\n    text-transform: uppercase;\n    color: var(--subtle);\n  }\n  .model-lineup__grid {\n    display: grid;\n    gap: 12px;\n    grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));\n  }\n  .model-lineup__button {\n    border: 1px solid var(--border);\n    border-radius: 16px;\n    padding: 14px 16px;\n    background: var(--surface);\n    text-align: left;\n    display: grid;\n    gap: 4px;\n    transition: transform 0.18s ease, box-shadow 0.18s ease, border-color 0.18s ease;\n  }\n  .model-lineup__button:hover {\n    border-color: var(--accent);\n    box-shadow: 0 14px 36px rgba(29, 78, 216, 0.14);\n    transform: translateY(-1px);\n  }\n  .model-lineup__button.is-active {\n    border-color: var(--accent);\n    box-shadow: 0 16px 36px rgba(29, 78, 216, 0.18);\n    background: rgba(29, 78, 216, 0.08);\n  }\n  .model-lineup__name {\n    font-weight: 600;\n    color: var(--text);\n    font-size: 0.95rem;\n  }\n  .model-lineup__tag {\n    font-size: 0.82rem;\n    color: var(--muted);\n  }\n  @media (max-width: 640px) {\n    .model-lineup__grid {\n      grid-template-columns: 1fr;\n    }\n  }\n  @media (min-width: 880px) {\n    .model-inspector {\n      grid-template-columns: minmax(0, 1.25fr) minmax(0, 1fr);\n      align-items: start;\n    }\n  }\n";
