export declare const MODEL_INSPECTOR_STYLES = "\n  .model-inspector {\n    display: grid;\n    gap: 24px;\n    margin: 16px 0 0;\n  }\n  @media (min-width: 960px) {\n    .model-inspector {\n      grid-template-columns: minmax(0, 3fr) minmax(0, 2fr);\n      align-items: start;\n    }\n  }\n  .model-inspector__detail,\n  .model-inspector__lineup {\n    min-width: 0;\n  }\n  .model-lineup {\n    display: grid;\n    gap: 14px;\n    padding: 18px 20px;\n    border-radius: 18px;\n    border: 1px solid var(--border);\n    background: var(--surface);\n    box-shadow: 0 18px 36px rgba(15, 23, 42, 0.06);\n  }\n  .model-lineup[hidden] {\n    display: none;\n  }\n  .model-lineup__title {\n    font-size: 0.78rem;\n    letter-spacing: 0.08em;\n    text-transform: uppercase;\n    color: var(--subtle);\n    font-weight: 600;\n  }\n  .model-lineup__grid {\n    display: grid;\n    gap: 10px;\n  }\n  .model-lineup__button {\n    appearance: none;\n    border: 1px solid rgba(148, 163, 184, 0.32);\n    background: rgba(15, 23, 42, 0.02);\n    border-radius: 16px;\n    padding: 14px 16px;\n    display: grid;\n    gap: 6px;\n    text-align: left;\n    cursor: pointer;\n    transition: transform 0.2s ease, box-shadow 0.2s ease, border-color 0.2s ease;\n  }\n  .model-lineup__button:hover {\n    transform: translateY(-2px);\n    box-shadow: 0 12px 24px rgba(15, 23, 42, 0.1);\n    border-color: rgba(59, 130, 246, 0.5);\n  }\n  .model-lineup__button:focus-visible {\n    outline: 2px solid var(--accent);\n    outline-offset: 2px;\n  }\n  .model-lineup__button.is-active {\n    border-color: rgba(59, 130, 246, 0.65);\n    background: rgba(59, 130, 246, 0.12);\n    box-shadow: inset 0 0 0 1px rgba(59, 130, 246, 0.18);\n  }\n  .model-lineup__name {\n    font-weight: 600;\n    color: var(--text);\n    font-size: 0.95rem;\n  }\n  .model-lineup__tag {\n    font-size: 0.8rem;\n    color: var(--subtle);\n  }\n  .model-detail {\n    border-radius: 18px;\n    border: 1px solid var(--border);\n    background: var(--surface-glass);\n    padding: 18px 20px;\n    box-shadow: 0 18px 36px rgba(15, 23, 42, 0.08);\n  }\n  .model-detail__header {\n    display: flex;\n    flex-wrap: wrap;\n    align-items: flex-start;\n    column-gap: 16px;\n    row-gap: 10px;\n  }\n  .model-detail__header > div:first-child {\n    flex: 1 1 260px;\n    min-width: 0;\n  }\n  .model-detail__meta {\n    display: flex;\n    flex-direction: column;\n    gap: 6px;\n    align-items: flex-end;\n    margin-left: auto;\n  }\n  .model-detail__header h3 {\n    margin: 0;\n    font-size: 1.1rem;\n    color: var(--text);\n  }\n  .model-detail__tagline {\n    margin: 4px 0 0;\n    font-size: 0.88rem;\n    color: var(--subtle);\n  }\n  .model-detail__cost {\n    font-weight: 600;\n    color: var(--accent);\n    font-size: 0.9rem;\n    margin-left: auto;\n    text-align: right;\n    line-height: 1.35;\n    white-space: normal;\n  }\n  .model-detail__description {\n    margin: 12px 0 10px;\n    color: var(--muted);\n    line-height: 1.6;\n  }\n  .model-capabilities {\n    display: flex;\n    flex-wrap: wrap;\n    align-items: center;\n    gap: 10px;\n    margin: 0 0 14px;\n  }\n  .model-capability {\n    --cap-bg: rgba(148, 163, 184, 0.12);\n    --cap-fg: var(--muted);\n    --cap-badge-bg: rgba(148, 163, 184, 0.22);\n    --cap-badge-shadow: inset 0 0 0 1px rgba(148, 163, 184, 0.32);\n    --cap-icon-color: rgba(148, 163, 184, 0.9);\n    --cap-border: rgba(148, 163, 184, 0.35);\n    display: inline-flex;\n    align-items: center;\n    gap: 10px;\n    border-radius: 999px;\n    padding: 6px 14px 6px 8px;\n    font-size: 0.78rem;\n    font-weight: 600;\n    letter-spacing: 0.05em;\n    text-transform: uppercase;\n    background: var(--cap-bg);\n    color: var(--cap-fg);\n    border: 1px solid var(--cap-border);\n    transition: background 0.18s ease, color 0.18s ease, box-shadow 0.18s ease, border-color 0.18s ease;\n  }\n  .model-capability__badge {\n    width: 28px;\n    height: 28px;\n    border-radius: 12px;\n    display: grid;\n    place-items: center;\n    background: var(--cap-badge-bg);\n    box-shadow: var(--cap-badge-shadow);\n  }\n  .model-capability__svg {\n    width: 16px;\n    height: 16px;\n    stroke: var(--cap-icon-color);\n    stroke-width: 1.8;\n    stroke-linecap: round;\n    stroke-linejoin: round;\n  }\n  .model-capability__label {\n    display: inline-flex;\n    gap: 6px;\n    align-items: center;\n    letter-spacing: 0.06em;\n  }\n  .model-capability--enabled {\n    --cap-bg: rgba(59, 130, 246, 0.14);\n    --cap-fg: var(--accent-dark);\n    --cap-badge-bg: var(--cap-badge-active, rgba(59, 130, 246, 0.28));\n    --cap-badge-shadow: 0 10px 20px rgba(37, 99, 235, 0.18);\n    --cap-icon-color: var(--cap-icon-active, var(--accent));\n    --cap-border: rgba(59, 130, 246, 0.45);\n    box-shadow:\n      inset 0 0 0 1px rgba(59, 130, 246, 0.2),\n      0 10px 24px rgba(59, 130, 246, 0.18);\n  }\n  .model-capability--disabled {\n    --cap-bg: rgba(148, 163, 184, 0.12);\n    --cap-fg: rgba(148, 163, 184, 0.9);\n    --cap-badge-bg: rgba(148, 163, 184, 0.22);\n    --cap-badge-shadow: inset 0 0 0 1px rgba(148, 163, 184, 0.32);\n    --cap-icon-color: rgba(148, 163, 184, 0.9);\n    --cap-border: rgba(148, 163, 184, 0.3);\n    opacity: 0.75;\n  }\n  .model-capability--unknown {\n    --cap-bg: rgba(250, 204, 21, 0.14);\n    --cap-fg: #9a6700;\n    --cap-badge-bg: rgba(250, 204, 21, 0.26);\n    --cap-badge-shadow: inset 0 0 0 1px rgba(251, 191, 36, 0.6);\n    --cap-icon-color: #d97706;\n    --cap-border: rgba(251, 191, 36, 0.4);\n  }\n  .model-capability--type-reasoning {\n    --cap-badge-active: linear-gradient(135deg, rgba(59, 130, 246, 0.3) 0%, rgba(96, 165, 250, 0.58) 100%);\n    --cap-icon-active: #2563eb;\n  }\n  .model-capability--type-images {\n    --cap-badge-active: linear-gradient(135deg, rgba(6, 182, 212, 0.28) 0%, rgba(56, 189, 248, 0.52) 100%);\n    --cap-icon-active: #0ea5e9;\n  }\n  .model-scores {\n    margin: 0 0 18px;\n    padding: 12px 14px;\n    border-radius: 14px;\n    border: 1px solid rgba(148, 163, 184, 0.24);\n    background: rgba(15, 23, 42, 0.03);\n  }\n  .model-score-list {\n    display: grid;\n    gap: 10px;\n  }\n  .model-score {\n    --score-fill: 0;\n    display: grid;\n    gap: 4px;\n    font-variant-numeric: tabular-nums;\n  }\n  .model-score__header {\n    display: flex;\n    align-items: center;\n    justify-content: space-between;\n    gap: 10px;\n  }\n  .model-score__label {\n    font-size: 0.72rem;\n    letter-spacing: 0.08em;\n    text-transform: uppercase;\n    color: var(--subtle);\n    font-weight: 600;\n  }\n  .model-score__value {\n    font-size: 0.82rem;\n    font-weight: 600;\n    color: var(--accent-dark);\n  }\n  .model-score__meter {\n    position: relative;\n    height: 4px;\n    border-radius: 999px;\n    background: rgba(148, 163, 184, 0.2);\n    overflow: hidden;\n  }\n  .model-score__fill {\n    position: absolute;\n    inset: 0;\n    width: calc(var(--score-fill) * 100%);\n    border-radius: inherit;\n    background: linear-gradient(90deg, rgba(59, 130, 246, 0.18) 0%, var(--accent) 100%);\n    transition: width 0.2s ease;\n  }\n  .model-score__descriptor {\n    font-size: 0.7rem;\n    letter-spacing: 0.06em;\n    text-transform: uppercase;\n    color: var(--muted);\n  }\n  .model-scores__empty {\n    margin: 0;\n    color: var(--muted);\n    font-size: 0.85rem;\n  }\n  .model-detail__facts {\n    display: grid;\n    gap: 10px;\n    margin: 0;\n    padding: 0;\n  }\n  .model-detail__facts div {\n    display: grid;\n    gap: 4px;\n  }\n  .model-detail__facts dt {\n    margin: 0;\n    font-size: 0.7rem;\n    text-transform: uppercase;\n    letter-spacing: 0.08em;\n    color: var(--subtle);\n  }\n  .model-detail__facts dd {\n    margin: 0;\n    font-weight: 500;\n    color: var(--text);\n  }\n  .model-highlight {\n    display: inline-flex;\n    align-items: center;\n    padding: 0.2rem 0.6rem;\n    border-radius: 999px;\n    background: var(--accent-soft);\n    color: var(--accent-dark);\n    font-size: 0.75rem;\n    font-weight: 600;\n  }\n";
